			pointer-events: none;
		}

:host {
			display: inline-block;
			min-width: 24px;
			min-height: 24px;
		}

		:host([theme='light']) svg g#NavbarFooter-blizzard-lower polygon {
			fill: var(--nav-color-darken-900);
		}

		svg {
			pointer-events: none;
			max-width: 150px;
			width: 100%;
		}

		svg g#NavbarFooter-blizzard-lower * {
			fill: var(--footer-logo-bottom-color);
		}

		svg g#NavbarFooter-blizzard-upper * {
			fill: var(--footer-logo-color);
		}

(() => {
  const blogContent = document.querySelector('.Content .blog');
  const images = blogContent && blogContent.querySelectorAll('img[src*=".png"], img[src*=".gif"], img[src*=".PNG"], img[src*=".GIF"]');

  if (images) {
    images.forEach((img) => {
      img.classList.add('no-border', 'no-shadow');
    });
  }
})()

